package test;

public class SampleC {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=5;
		while(x>=1) {
			System.out.println(x);
			x=x-1;     //decrement
		}
		double y=0.5;
		while(y>=0.1) {
			System.out.println(y);
			y=y-0.1;
		}
		int z=-1;
		while(z>=-5) {
			System.out.println(z);
			z=z-1;
		}
		int a=-5;
		while(a<=-1) {
			System.out.println(a);
			a=a+1;
		}

	}

}
